package day06_relationalOperators_ifstatements;

public class C1_RelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean sonuc1 = 5 + 2 ==7;
		
		System.out.println(sonuc1);
		
		System.out.println(15*3==40);
		
		System.out.println(15-3==12);
		
		//sisteme bir değere atama yapmadan direk == i yazdırırsan sistem bunun boolean olduğunu anlar ve boolean şeklinde 
		//bir değere atama yapmaksızın da sonuç elde ederiz.
		
		boolean esitMi= 24/2==12;
		
		System.out.println(esitMi);
		
		System.out.println(5+2>8 || 9<6 );
		System.out.println(5+2>8 || 9<6 );
		System.out.println(5+2>8 || 9<6 );
		
		
		
		
	}

}
